var cleanMessage = function(message) {
  return message.replace(/\\"/g,'"').replace(/(^"|"$)+/g,'').replace(/\\n$/g,'');
}

module.exports = {
	handle: function(eventPayload) {
          console.log(eventPayload);
          eventPayload.message = cleanMessage(eventPayload.message);
          return eventPayload;
	}
};
